SELECT loan_applications.id as "ID", origination_service_loan_number as "ML Loan Number", last_name as "Last Name", first_name as "First Name", status as "Status", state as "State", zip as "Zip", publisher as "Publisher", partner_metadata -> 'click_id' as "Click ID" from loan_applications left join addresses on loan_applications.id = addresses.loan_application_id where bank_partner = {{ bankPartner}};